CREATE TABLE categories (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  display_order BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  version BIGINT NOT NULL,
  tag VARCHAR(32) NOT NULL,
  name VARCHAR(100) NOT NULL,
  description VARCHAR(1000) NOT NULL,
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;



CREATE TABLE articles (
  id BIGINT NOT NULL,
  category_id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  image_id BIGINT NOT NULL,
  publish_at BIGINT NOT NULL,
  text_id BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  user_id BIGINT NOT NULL,
  version BIGINT NOT NULL,
  views BIGINT NOT NULL,
  name VARCHAR(100) NOT NULL,
  tags VARCHAR(100) NOT NULL,
  description VARCHAR(1000) NOT NULL,
  INDEX IDX_CAT_PUB (category_id,publish_at),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;

CREATE TABLE texts (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  version BIGINT NOT NULL,
  hash VARCHAR(64) NOT NULL,
  content TEXT NOT NULL,
  CONSTRAINT UNI_HASH UNIQUE (hash),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;


CREATE TABLE users (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  locked_until BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  VERSION BIGINT NOT NULL,
  role VARCHAR(50) NOT NULL,
  email VARCHAR(100) NOT NULL,
  NAME VARCHAR(100) NOT NULL,
  image_url VARCHAR(1000) NOT NULL,
  CONSTRAINT UNI_EMAIL UNIQUE (email),
  PRIMARY KEY(id)
) ENGINE=INNODB DEFAULT CHARSET=UTF8MB4;

CREATE TABLE local_auths (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  user_id BIGINT NOT NULL,
  version BIGINT NOT NULL,
  passwd VARCHAR(64) NOT NULL,
  salt VARCHAR(64) NOT NULL,
  CONSTRAINT UNI_UID UNIQUE (user_id),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8;


CREATE TABLE attachments (
  id BIGINT NOT NULL,
  height INTEGER NOT NULL,
  width INTEGER NOT NULL,
  created_at BIGINT NOT NULL,
  resource_id BIGINT NOT NULL,
  size BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  user_id BIGINT NOT NULL,
  version BIGINT NOT NULL,
  mime VARCHAR(100) NOT NULL,
  name VARCHAR(100) NOT NULL,
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;

CREATE TABLE resources (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  version BIGINT NOT NULL,
  encoding VARCHAR(50) NOT NULL,
  hash VARCHAR(64) NOT NULL,
  content MEDIUMTEXT NOT NULL,
  CONSTRAINT UNI_HASH UNIQUE (hash),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8;




CREATE TABLE topics (
  id BIGINT NOT NULL,
  locked BOOL NOT NULL,
  board_id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  ref_id BIGINT NOT NULL,
  reply_number BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  user_id BIGINT NOT NULL,
  version BIGINT NOT NULL,
  ref_type VARCHAR(50) NOT NULL,
  name VARCHAR(100) NOT NULL,
  user_name VARCHAR(100) NOT NULL,
  user_imageUrl VARCHAR(1000) NOT NULL,
  content TEXT NOT NULL,
  INDEX IDX_board_id (board_id),
  INDEX IDX_ref_id (ref_id),
  INDEX IDX_updated_at (updated_at),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;



CREATE TABLE replies (
  id BIGINT NOT NULL,
  created_at BIGINT NOT NULL,
  topic_id BIGINT NOT NULL,
  updated_at BIGINT NOT NULL,
  user_id BIGINT NOT NULL,
  version BIGINT NOT NULL,
  user_name VARCHAR(100) NOT NULL,
  user_imageUrl VARCHAR(1000) NOT NULL,
  content TEXT NOT NULL,
  INDEX IDX_topic_id (topic_id),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;






CREATE TABLE boards (
  id BIGINT NOT NULL,
  locked BOOL NOT NULL,
  createdAt BIGINT NOT NULL,
  displayOrder BIGINT NOT NULL,
  topicNumber BIGINT NOT NULL,
  updatedAt BIGINT NOT NULL,
  version BIGINT NOT NULL,
  tag VARCHAR(32) NOT NULL,
  name VARCHAR(100) NOT NULL,
  description VARCHAR(1000) NOT NULL,
  CONSTRAINT UNI_TAG UNIQUE (tag),
  PRIMARY KEY(id)
) Engine=INNODB DEFAULT CHARSET=UTF8MB4;
